#include <stdio.h>

int main(void)
{
    double gpa = 0.0;
    int incidents = 0;

    printf("Enter cumulative GPA:\n");
    if (scanf("%lf", &gpa) != 1) {
        printf("Invalid input.\n");
        return 1;
    }

    printf("Enter disciplinary incidents:\n");
    if (scanf("%d", &incidents) != 1) {
        printf("Invalid input.\n");
        return 1;
    }

    if (gpa < 0.0 || gpa > 4.0) {
        printf("Invalid ranges.\n");
        return 1;
    }

    if (incidents < 0) {
        incidents = 0;
    }

    int statusCode = 0;
    const char *statusLabel = NULL;

    if (gpa >= 3.5 && incidents == 0) {
        statusCode = 0;
        statusLabel = "Honours";
    } else if (gpa >= 2.5 && incidents <= 1) {
        statusCode = 1;
        statusLabel = "Good";
    } else if (gpa >= 2.0 && incidents <= 2) {
        statusCode = 2;
        statusLabel = "Needs Support";
    } else {
        statusCode = 3;
        statusLabel = "Probation";
    }

    printf("Status: %s\n", statusLabel);

    switch (statusCode) {
        case 0:
            printf("Meet honours advisor.\n");
            break;
        case 1:
            printf("Keep strong progress.\n");
            break;
        case 2:
            printf("Enroll in support workshops.\n");
            break;
        default:
            printf("Schedule dean review.\n");
            break;
    }

    printf("Enter action (p=plan, r=resources, q=quit):\n");

    int action = getchar();
    while (action == '\n' || action == '\r') {
        action = getchar();
    }

    switch (action) {
        case 'p':
        case 'P':
            printf("Planning session underway.\n");
            break;
        case 'r':
        case 'R':
            printf("Resources email sent.\n");
            break;
        case 'q':
        case 'Q':
            printf("Good luck this term!\n");
            break;
        default:
            printf("Unknown action.\n");
            break;
    }

    return 0;
}
