#include <stdio.h>

int add(int a, int b)
{
    return a + b;
}

int subtract(int a, int b)
{
    return a - b;
}

int multiply(int a, int b)
{
    return a * b;
}

int divide(int a, int b)
{
    if (b == 0)
    {
        printf("Error: Division by zero.\n");
        return 0;
    }
    return a / b;
}

int main(void)
{
    char op;
    int a, b;

    if (scanf("%c", &op) != 1)
    {
        printf("Invalid input.\n");
        return 1;
    }

    if (scanf("%d", &a) != 1 || scanf("%d", &b) != 1)
    {
        printf("Invalid numbers.\n");
        return 1;
    }

    int result = 0;
    int valid = 1;

    switch (op)
    {
        case '+':
            result = add(a, b);
            break;
        case '-':
            result = subtract(a, b);
            break;
        case '*':
            result = multiply(a, b);
            break;
        case '/':
            result = divide(a, b);
            if (b == 0)
            {
                valid = 0;
            }
            break;
        default:
            printf("Invalid operation.\n");
            valid = 0;
            break;
    }

    if (valid)
    {
        printf("Result: %d\n", result);
    }

    return 0;
}
