#include <stdio.h>

#define ADD(a,b) ((a) + (b))
#define SUB(a,b) ((a) - (b))
#define MUL(a,b) ((a) * (b))
#define DIV(a,b) ((a) / (b))

int main(void)
{
    char op;
    int a, b;
    if (scanf("%c %d %d", &op, &a, &b) != 3)
    {
        printf("Invalid input\n");
        return 1;
    }

    switch (op)
    {
        case '+':
            printf("%d\n", ADD(a, b));
            break;
        case '-':
            printf("%d\n", SUB(a, b));
            break;
        case '*':
            printf("%d\n", MUL(a, b));
            break;
        case '/':
            printf("%d\n", DIV(a, b));
            break;
        default:
            printf("Invalid\n");
            break;
    }

    return 0;
}
