#include <iostream>
#include <iomanip>
#include <limits>

int main() {
    std::cout << std::left;

    const int seats = 24;
    const unsigned int tablets = 32u;
    const double passRate = 92.3;
    const char initial = 'R';

    std::cout << "Type Report: C++ Fundamentals\n";
    std::cout << "Seats (int): " << seats
              << " (bytes: " << sizeof(int)
              << ", min: " << std::numeric_limits<int>::min()
              << ", max: " << std::numeric_limits<int>::max()
              << ")\n";

    std::cout << "Tablets (unsigned): " << tablets
              << " (bytes: " << sizeof(unsigned int)
              << ", min: " << std::numeric_limits<unsigned int>::min()
              << ", max: " << std::numeric_limits<unsigned int>::max()
              << ")\n";

    std::cout << "Pass Rate (double): " << passRate
              << " (bytes: " << sizeof(double)
              << ", min: " << std::numeric_limits<double>::lowest()
              << ", max: " << std::numeric_limits<double>::max()
              << ")\n";

    const int charMin = 0;
    const int charMax = 127;

    std::cout << "Initial (char): " << initial
              << " (bytes: " << sizeof(char)
              << ", min: " << charMin
              << ", max: " << charMax
              << ")";

    return 0;
}
