#include <iostream>
#include <string>

int main() {
    int score{};

    std::cout << "Enter score:\n";
    std::cin >> score;

    std::string result{};

    if (score < 0 || score > 100) {
        result = "Invalid score";
    } else if (score >= 90) {
        result = "Outstanding";
    } else if (score >= 80) {
        result = "Great";
    } else if (score >= 60) {
        result = "Satisfactory";
    } else {
        result = "Needs improvement";
    }

    std::cout << "Result: " << result;

    return 0;
}
