#include <iostream>
#include <iomanip>

int triple(int value) {
    return value * 3;
}

double average(double first, double second) {
    return (first + second) / 2.0;
}

int main() {
    std::cout << std::fixed << std::setprecision(1);

    std::cout << "Triple of 7: " << triple(7) << '\n';
    std::cout << "Average of 4.0 and 6.0: " << average(4.0, 6.0);

    return 0;
}
