#include <iostream>

int main() {
    int n{};

    std::cout << "Enter a positive integer:\n";
    std::cin >> n;

    int sum{0};

    std::cout << "Sequence: ";
    for (int i{1}; i <= n; ++i) {
        sum += i;
        std::cout << i;
        if (i < n) {
            std::cout << ' ';
        }
    }
    std::cout << '\n';

    std::cout << "Sum: " << sum;

    return 0;
}
