#include <iostream>

int main() {
    int limit = 0;
    std::cin >> limit;

    std::cout << "Limit: " << limit << std::endl;

    if (limit <= 0) {
        std::cout << "Series: n/a" << std::endl;
        std::cout << "Sum: 0" << std::endl;
        return 0;
    }

    int sum = 0;

    std::cout << "Series: ";
    for (int value = 1; value <= limit; ++value) {
        if (value > 1) {
            std::cout << ' ';
        }
        std::cout << value;
        sum += value;
    }
    std::cout << std::endl;

    std::cout << "Sum: " << sum << std::endl;
    return 0;
}
