fn take_ownership(s: String) -> usize {
    // Return the length of the string
    // This function takes ownership of s
}

fn borrow_string(s: &String) -> usize {
    // Return the length of the string
    // This function borrows s
}

fn main() {
    let s1 = String::from("hello");
    let len1 = take_ownership(s1);
    println!("Length from ownership: {}", len1);
    // s1 is moved and cannot be used here

    let s2 = String::from("world");
    let len2 = borrow_string(&s2);
    println!("Length from borrow: {}", len2);
    // s2 is still accessible here
}
